import React, { useState, useEffect } from "react";
import { getTrips } from "../api";
import TripCard from "../components/TripCard";
import BookingForm from "../components/BookingForm";

export default function HomePage({ onGoAdmin }) {
  const [trips, setTrips] = useState([]);
  const [selectedTrip, setSelectedTrip] = useState(null);
  const [confirmation, setConfirmation] = useState(null);

  useEffect(() => {
    (async () => {
      try {
        const data = await getTrips();
        setTrips(data);
      } catch (err) {
        console.error(err);
      }
    })();
  }, []);

  const handleBooked = (booking) => {
    setConfirmation(booking);
    setSelectedTrip(null);
  };

  return (
    <div className="container">
      <h1>Trip Plans</h1>
      <button onClick={onGoAdmin}>Go to Admin Panel</button>

      {confirmation && (
        <div className="confirmation">
          <h2>Booking Confirmed</h2>
          <p>Trip: {confirmation.tripName}</p>
          <p>Date: {confirmation.date}</p>
          <p>Name: {confirmation.userName}</p>
        </div>
      )}

      {trips.map(trip => (
        <TripCard key={trip._id} trip={trip} onSelect={setSelectedTrip} />
      ))}

      {selectedTrip && (
        <BookingForm trip={selectedTrip} onBooked={handleBooked} />
      )}
    </div>
  );
}
