import React, { useState } from "react";
import { checkAvailability, createBooking } from "../api";

export default function BookingForm({ trip, onBooked }) {
  const [date, setDate] = useState("");
  const [userName, setUserName] = useState("");
  const [availability, setAvailability] = useState(null);

  const handleCheckAvailability = async () => {
    if (!date) return alert("Please select a date");
    try {
      const data = await checkAvailability(trip._id, date);
      setAvailability(data.available);
    } catch (err) {
      alert(err.message);
    }
  };

  const handleBook = async () => {
    if (!userName) return alert("Please enter your name");
    if (!availability) return alert("No vans available for this date");

    try {
      const booking = await createBooking({
        tripId: trip._id,
        userName,
        date
      });
      onBooked(booking);
    } catch (err) {
      alert(err.message);
    }
  };

  return (
    <div className="booking-form">
      <h2>Booking for {trip.title}</h2>
      <input
        type="date"
        value={date}
        onChange={e => setDate(e.target.value)}
      />
      <input
        type="text"
        placeholder="Your Name"
        value={userName}
        onChange={e => setUserName(e.target.value)}
      />
      <button onClick={handleCheckAvailability}>Check Availability</button>
      {availability !== null && (
        availability ? (
          <div>
            <p>Vans available! Proceed to book.</p>
            <button onClick={handleBook}>Book Now</button>
          </div>
        ) : (
          <p>No vans available for the selected date.</p>
        )
      )}
    </div>
  );
}
