import React, { useState, useEffect } from "react";
import { getTrips, checkAvailability, bookTrip, getBookings } from "./api";
import "./App.css";

export default function App() {
  const [trips, setTrips] = useState([]);
  const [selectedTrip, setSelectedTrip] = useState(null);
  const [date, setDate] = useState("");
  const [userName, setUserName] = useState("");
  const [availability, setAvailability] = useState(null);
  const [confirmation, setConfirmation] = useState(null);
  const [adminBookings, setAdminBookings] = useState([]);
  const [view, setView] = useState("home"); // home, admin

  useEffect(() => {
    getTrips().then(res => setTrips(res.data));
  }, []);

  const handleCheckAvailability = () => {
    if (!date) return alert("Please select a date");
    checkAvailability(date).then(res => setAvailability(res.data.available));
  };

  const handleBook = () => {
    if (!userName) return alert("Please enter your name");
    bookTrip(selectedTrip.id, userName, date)
      .then(res => {
        setConfirmation(res.data.booking);
        setAvailability(null);
        setSelectedTrip(null);
        setDate("");
        setUserName("");
      })
      .catch(err => alert(err.response.data.error));
  };

  const loadAdminBookings = () => {
    getBookings().then(res => setAdminBookings(res.data));
  };

  if (view === "admin") {
    return (
      <div className="container">
        <h1>Admin Panel</h1>
        <button onClick={() => setView("home")}>Back to Home</button>
        <button onClick={loadAdminBookings}>Load Bookings</button>
        <ul>
          {adminBookings.map(b => (
            <li key={b.id}>
              {b.tripName} - {b.userName} - {b.date} - {b.status}
            </li>
          ))}
        </ul>
      </div>
    );
  }

  return (
    <div className="container">
      <h1>Trip Plans</h1>
      <button onClick={() => setView("admin")}>Go to Admin Panel</button>

      {confirmation && (
        <div className="confirmation">
          <h2>Booking Confirmed</h2>
          <p>Trip: {confirmation.tripName}</p>
          <p>Date: {confirmation.date}</p>
          <p>Name: {confirmation.userName}</p>
        </div>
      )}

      {trips.map(trip => (
        <div key={trip.id} className="trip-card">
          <img src={trip.image} alt={trip.title} className="trip-image" />
          <h3>{trip.title}</h3>
          <p>{trip.description}</p>
          <p><strong>Price:</strong> ₹{trip.price}</p>
          <p><strong>Duration:</strong> {trip.durationDays} days</p>
          <button onClick={() => setSelectedTrip(trip)}>Select Trip</button>
        </div>
      ))}

      {selectedTrip && (
        <div className="booking-form">
          <h2>Booking for {selectedTrip.title}</h2>
          <input
            type="date"
            value={date}
            onChange={e => setDate(e.target.value)}
          />
          <input
            type="text"
            placeholder="Your Name"
            value={userName}
            onChange={e => setUserName(e.target.value)}
          />
          <button onClick={handleCheckAvailability}>Check Availability</button>
          {availability !== null && (
            <div>
              {availability ? (
                <div>
                  <p>Vans available! Proceed to book.</p>
                  <button onClick={handleBook}>Book Now</button>
                </div>
              ) : (
                <p>No vans available for the selected date.</p>
              )}
            </div>
          )}
        </div>
      )}
    </div>
  );
}
